import React , {useEffect,useState } from 'react';

import { getProducts } from './helper/Coreapicalls';
import Base from './Base';
import Card from './Card';
import "../styles.css";

export default function Home() {

  const [products,setProducts]=useState([]);
  const [error,setError]=useState(false);


  const loadallProducts = () => {
    getProducts()
      .then((data) => {
        if (data && data.error) {
          setError(data.error);
          console.log(error);
        } else {
          
          setProducts(data);
        }
      });
  };

  useEffect(() => {
		loadallProducts();
	}, []);

  return (
    <Base>
      
        <div className='row'>
      <div className='col-sm-3 '>
      <div className="card">
        <article className="card-group-item">
          <header className="card-header"><h6 className="title">Similar category </h6></header>
          <div className="filter-content">
            <div className="list-group list-group-flush">
              <a href="#" className="list-group-item">Cras justo odio <span className="float-right badge badge-light round">142</span> </a>
              <a href="#" className="list-group-item">Dapibus ac facilisis  <span className="float-right badge badge-light round">3</span></a>
              <a href="#" className="list-group-item">Morbi leo risus <span className="float-right badge badge-light round">32</span></a>
              <a href="#" className="list-group-item">Another item <span className="float-right badge badge-light round">12</span></a>
            </div>  {/* list-group .// */}
          </div>
        </article> {/* card-group-item.// */}
        <article className="card-group-item">
          <header className="card-header"><h6 className="title">Color check</h6></header>
          <div className="filter-content">
            <div className="card-body">
              <label className="btn btn-danger">
                <input className type="checkbox" name="myradio" defaultValue />
                <span className="form-check-label">Red</span>
              </label>
              <label className="btn btn-success">
                <input className type="checkbox" name="myradio" defaultValue />
                <span className="form-check-label">Green</span>
              </label>
              <label className="btn btn-primary">
                <input className type="checkbox" name="myradio" defaultValue />
                <span className="form-check-label">Blue</span>
              </label>
            </div> {/* card-body.// */}
          </div>
        </article> {/* card-group-item.// */}
      </div>
      </div>
       <div className="col">
      

        {products.map((product,index)=>
        
        {
          return (
            
            
            <div key={index} className="col-3">  
                <Card product={product}/>
              
                <br>
                </br>
                <br>
                </br>
              </div>
          )
        })}
      </div>
      </div>
      
     
      
      
    </Base>
  ); 
}
