import React, { useEffect, useState } from "react";
import "../styles.css";
import Base from "./Base";
import ImageHelper from "./helper/Image";
import { addItemToCart, addItemToCartnew, addunit, loadcart, removeitemfromcart, removeunit } from "./helper/Carthelper";
import {Link,  Navigate} from "react-router-dom";


const Shoppingcart =() => {
  const [products, setProducts] = useState([]);
  const [pquantiy,setPquantity] =useState(1);
  const [reload,setReload]=useState(false);
  const [reloadunit,setReloadunit]=useState(false);                       //for adding unit
  const [reloadunitmin,setReloadunitmin]=useState(false);                  //for minus unit
  
  const {unit} =products;



  const quantityadd = (prod)  => 
  { 
    
    addunit(prod)
    
  }

  const quantitymin = (prod) =>
  {
    removeunit(prod)
   
  }

  const pricehandler = () => {
    let nprice = 0;

    products.map((product) => {
      nprice = nprice + (parseInt(product.price)*product.stock);
    });
    
    return nprice;
  };


  const unithandler = () => {
    let unitaddition = 0;

    products.map((product) => {
      unitaddition = unitaddition + product.stock;
    });
    
    return unitaddition;
  };





  useEffect(() => {
    setProducts(loadcart());
    unithandler();
  }, [reload,reloadunit,reloadunitmin]);
  return (
    <Base>
     <section className="h-100 gradient-custom">
    <div className="container py-5">
      <div className="row d-flex justify-content-center my-4">
      <div className="col-md-8">

            
<div className="card mb-4">
  <div className="card-header py-3">
    <h5 className="mb-0">Cart - 2 items</h5>
  </div>
  <div className="card-body">
    {/* Single item */}
    <div className="row">
      <div className="col-lg-3 col-md-12 mb-4 mb-lg-0">
        {/* Image */}
        <div className="bg-image hover-overlay hover-zoom ripple rounded" data-mdb-ripple-color="light">
          <img src="https://mdbcdn.b-cdn.net/img/Photos/Horizontal/E-commerce/Vertical/12a.webp" className="w-100" alt="Blue Jeans Jacket" />
          <a href="#!">
            <div className="mask" style={{backgroundColor: 'rgba(251, 251, 251, 0.2)'}} />
          </a>
        </div>
        {/* Image */}
      </div>
      <div className="col-lg-5 col-md-6 mb-4 mb-lg-0">
        {/* Data */}
        <p><strong>Blue denim shirt</strong></p>
        <p>Color: blue</p>
        <p>Size: M</p>
        <button type="button" className="btn btn-primary btn-sm me-1 mb-2" data-mdb-toggle="tooltip" title="Remove item">
          <i className="fa fa-trash" />
        </button>
        <button type="button" className="btn btn-danger btn-sm mb-2" data-mdb-toggle="tooltip" title="Move to the wish list">
          <i className="fa fa-heart" />
        </button>
        {/* Data */}
      </div>
      <div className="col-lg-4 col-md-6 mb-4 mb-lg-0">
        {/* Quantity */}
        <div className="d-flex mb-3" style={{maxWidth: '200px'}} >
          <button className="btn btn-primary px-3 me-2" onclick="this.parentNode.querySelector('input[type=number]').stepDown()">
            <i className="fa fa-minus" />
          </button>
          <div className="form-outline">
            <input id="form1" min={0} name="quantity" defaultValue={1}  className="form-control" style={{maxWidth: '40px'}}  readOnly={true}/>
          
          </div>
          <button className="btn btn-primary px-3 ms-2" onclick="this.parentNode.querySelector('input[type=number]').stepUp()">
            <i className="fa fa-plus" />
          </button>
        </div>
        {/* Quantity */}
        {/* Price */}
        <p className="text-start text-md-center">
          <strong>$17.99</strong>
        </p>
        {/* Price */}
