import React, { useEffect, useState } from "react";
import "../styles.css";
import Base from "./Base";
import ImageHelper from "./helper/Image";
import { loadcart, removeitemfromcart } from "./helper/Carthelper";


export default function Shoppingcart( {reload=undefined,
  setReload = f => f,}) {
  const [products, setProducts] = useState([]);
  const [pquantiy,setPquantity] =useState(1);

  const quantityadd = () =>
  {
    setPquantity(pquantiy+1);

  }

  const quantitymin = () =>
  {
    setPquantity(pquantiy-1);
  }

  const pricehandler = () => {
    let nprice = 0;

    products.map((product) => {
      nprice = nprice + parseInt(product.price);
    });
    
    return nprice;
  };




  useEffect(() => {
    setProducts(loadcart());
  }, []);
  return (
    <Base>
      <div className="containercart-body">
        <div className="Cart-Container">
          <div className="Headern">
            <h3 className="Headingn">Shopping Cart</h3>
          </div>
          {products.map((product, index) => (
            <div className="Cart-Items">
              <div className="image-box" style={{ height: "120px" }}>
                <ImageHelper product={product} />
              </div>
              <div className="about">
                <h2 className="titlen">{product.name}</h2>
                <h3 className="subtitle">{product.description}</h3>
              </div>
              <div className="counter">
                <div onClick={()=>quantityadd()} className="btnn">+</div>
                <div className="count">{pquantiy}</div>
                <div onClick={()=>quantitymin()} className="btnn">-</div>
              </div>
              <div className="pricesn">
                <div className="amount">{product.price}</div>

                <div onClick={()=>removeitemfromcart(product.id)} className="remove">
                  <u>Remove</u>
                </div>
              </div>
            </div>
          ))}
          <hr />

          <div className="checkout">
            <div className="total">
              <div>
                <div className="Subtotal">Sub-Total</div>
                <div className="itemsn">{products.length} items</div>
              </div>
              <div className="total-amount">{pricehandler()}</div>
            </div>
            <button className="buttonn">Checkout</button>
          </div>
        </div>
      </div>
    </Base>
  );
}
