import React, { useEffect, useState } from 'react';
import ImageHelper from './helper/Image';
import { Link,Navigate } from 'react-router-dom';
import { addItemToCart,removeitemfromcart } from './helper/Carthelper';
import useFetch from "react-fetch-hook"
import Product from './Product';


//TODO:
const isAuthenticated=true;




const Card =({
    product,
    addtocart=true,
    removefromcart=false,
    reload=undefined,
    setReload = f => f,
    //function(f){return f}
})=> {

  const [redirect,setRedirect]=useState(false)

    const addTocart=()=>{
        if (isAuthenticated)
        {
            addItemToCart(product,()=>setRedirect(true))
            console.log("added to cart")
        }
        else{
            console.log("login first!")
        }
    };

    const getredirect=(redirect)=>{
        if(redirect)
        {
            return <Navigate to="/cart"/>
        }
    };

    const showaddtocart = (addTocart) =>
    {
        return(
            addTocart && (
                <div className='col text-center'>
                    <div class=" bg-light text-right btn-group btn-group-toggle" data-toggle="buttons">
  <                 label class="btn btn-secondary active">
                    <input onClick={addTocart} type="radio" name="options" id="option1" autocomplete="off" checked/> Add to Cart
                    </label>
                    </div>
                    </div>
            )
        );
    };

    const showremovefromcart = removefromcart =>
    {
        return (
            removefromcart && 
            (
                <label class=" btn btn-secondary">
                <input type="radio" name="options" id="option2" autocomplete="off" onClick={()=>{
                    removeitemfromcart(product.id);
                    setReload(!reload)
                    console.log("will make it run");}}/> Remove
                </label>
            )
        );
    };
    
 
  
 
   
  return (
    <div>
    
      <div className="container">
        <div className="row justify-content-center">
          <div className="col col-xl">
            <div className="card text-black">
             
              {getredirect(redirect)}
              <div  className="rounded border border-success p-2"><ImageHelper product={product}/></div>
              
              <div  className="card-body">
                <div className="text-center">
                <Link to={`/products/${product.id}`}><h5 className="card-title">{product.name}</h5></Link>
                 
                  <span className="text-muted mb-4">{product.description}</span>
                </div>
               
                <div className="d-flex justify-content-between total font-weight-bold mt-4">
                  <span>Price</span><span>${product.price}</span>
    

                </div>
                <div className='col text-center'>
                    <div class=" bg-light text-right btn-group btn-group-toggle" data-toggle="buttons">
                       {showaddtocart(addTocart)}
                        {showremovefromcart(removefromcart)}
                </div>
                    </div>
              </div>
              
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
