import React,{useState} from 'react';
import Base from "../core/Base";
import {Link} from "react-router-dom";
import { signup } from '../auth/helper';
import "../styles.css";


const Signup=()=> {

    const [values,setValues]=useState({
        name:"",
        email:"",
        password:"",
        error:"",
        success:false,
    });

    const {name,email,password,error,success}= values;

    const handleChange = (name) => (event) => {
            setValues({...values,error:false,[name]:event.target.value});
    };
     
    

  return (
    <Base>

<div className="container">
        <div className="header">
          <h2>Sign Up</h2>
          <p>Already have an account? <span>Log in</span></p>
        </div>
        <div className="form">
          <div className="input">
            <label htmlFor="email">Email</label>
            <input id="email" type="text" />
          </div>
          <div className="input">
            <label htmlFor="email-confirm">Type your email again</label>
            <input id="email-confirm" type="text" />
          </div>
          <div className="input">
            <label htmlFor="password">Password</label>
            <input id="password" type="text" />
          </div>
          <div className="input">
            <label htmlFor="password-confirm">Type your password again</label>
            <input id="password-confirm" type="text" />
          </div>
        </div>
        <div className="actions">
          <button className="signup">Sign Up</button>
          <button className="google-login">Log in with Google</button>
        </div>
        <hr />
        <div className="forgot-password">
          <p>Forgot Password?</p>
        </div>
      </div>
    </Base>
  )
}


export default Signup;
