import React,{useEffect,useState}from "react";
import "../styles.css";
import Base from "./Base";
import ImageHelper from "./helper/Image";
import { loadcart } from './helper/Carthelper'


export default function Shoppingcart() {

    const [products,setProducts]=useState([])
    const [pprice ,setPprice] =useState(0)

    const pricehandler = () =>
    {
      let nprice=0;
      
      products.map((product)=>
      {
        console.log(product.prince)
        nprice=nprice+Number(product.price)
      })
      console.log(nprice)
      setPprice(nprice)
    }

    useEffect(()=>
        {
             setProducts(loadcart());
             pricehandler();
            },[])
  return (
    <Base>
    
    <div className="containercart-body">
      {console.log(pprice)}
     
      <div className="Cart-Container">
        <div className="Headern">
          <h3 className="Headingn">Shopping Cart</h3>
        </div>
        {products.map((product,index)=>(
            <div className="Cart-Items">
              
            <div className="image-box" style={{ height: "120px" }}>
            <ImageHelper product={product}/>
            
            </div>
            <div className="about">
            <h2 className="titlen">{product.name}</h2>
                <h3 className="subtitle">{product.description}</h3>
                </div>
            <div className="counter"> 
            <div className="btnn">+</div>
            <div className="count">1</div>
                <div className="btnn">-</div>
            </div>
            <div className="pricesn">
            <div className="amount">{product.price}</div>
           
     
            <div className="remove"><u>Remove</u></div>
            </div>
            </div>
        )
        )}
     <hr />
      <div className="checkout">
          <div className="total">
            <div>
              <div className="Subtotal">Sub-Total</div>
              <div className="itemsn">2 items</div>
            </div>
            <div className="total-amount">{pprice}</div>
          </div>
          <button className="buttonn">Checkout</button>
          </div>
        
      </div>
     
        
      </div>
     
    
    </Base>
  );
}
