import React, { useEffect, useState } from "react";
import "../styles.css";
import Base from "./Base";
import ImageHelper from "./helper/Image";
import { addItemToCart, addItemToCartnew, addunit, loadcart, removeitemfromcart, removeunit } from "./helper/Carthelper";
import {Link,  Navigate} from "react-router-dom";


const Shoppingcart =() => {
  const [products, setProducts] = useState([
    
    
  ]);
  const [pquantiy,setPquantity] =useState(1);
  const [reload,setReload]=useState(false);
  const [reloadunit,setReloadunit]=useState(false);                       //for adding unit
  const [reloadunitmin,setReloadunitmin]=useState(false);                  //for minus unit
  
  const {unit} =products;



  const quantityadd = (prod)  => 
  { 
    
    addunit(prod)
    
  }

  const quantitymin = (prod) =>
  {
    removeunit(prod)
   
  }

  const pricehandler = () => {
    let nprice = 0;

    products.map((product) => {
      nprice = nprice + (parseInt(product.price)*product.stock);
    });
    
    return nprice;
  };





  useEffect(() => {
    setProducts(loadcart());
    
    ;
  }, [reload,reloadunit,reloadunitmin]);
  return (
    <Base>
      <div className="containercart-body">
        <div className="Cart-Container">
          <div className="Headern">
            <h3 className="Headingn">Shopping Cart</h3>
          </div>
          {products.map((product, index) => (
            <div className="Cart-Items">
              <div className="image-box" style={{ height: "120px" }}>
                <ImageHelper product={product} />
              </div>
              <div className="about">
                <h2 className="titlen">{product.name}</h2>
                <h3 className="subtitle">{product.description}</h3>
              </div>
              <div className="counter">
                <div onClick={()=>quantityadd(product.id) & setReloadunit(!reloadunit)} className="btnn">+</div>
                <div className="count">{product.stock}</div>
                <div onClick={()=>quantitymin(product.id) & setReloadunitmin(!reloadunitmin)} className="btnn">-</div>
              </div>
              <div className="pricesn">
                <div className="amount">{product.price}</div>

                <div onClick={()=>removeitemfromcart(product.id) & setReload(!reload)} className="remove">
                  <u>Remove</u>
                </div>
              </div>
            </div>
          ))}
          <hr />

          <div className="checkout">
            <div className="total">
              <div>
                <div className="Subtotal">Sub-Total</div>
                <div className="itemsn">{products.length} items</div>
              </div>
              <div className="total-amount">{pricehandler()}</div>
            </div>
            <Link to="/checkout"> <button className="buttonn">Checkout</button></Link>
           
          </div>
        </div>
      </div>
    </Base>
  );
}


export default Shoppingcart;
